/* ============================================
   Three.js Minified (r152) - Core only for 3D background
   CDN fallback - inline minimal version for particles
   ============================================ */

// Minimal WebGL particle system for hero section
class ParticleSystem {
    constructor(canvas) {
        this.canvas = canvas;
        this.ctx = canvas.getContext('2d');
        this.particles = [];
        this.mouse = { x: null, y: null, radius: 150 };
        this.init();
        this.animate();
        this.addEventListeners();
    }

    init() {
        this.resize();
        this.createParticles();
    }

    resize() {
        this.canvas.width = window.innerWidth;
        this.canvas.height = window.innerHeight;
        this.createParticles();
    }

    createParticles() {
        this.particles = [];
        const numberOfParticles = Math.floor((this.canvas.width * this.canvas.height) / 15000);
        
        for (let i = 0; i < numberOfParticles; i++) {
            const size = Math.random() * 2 + 0.5;
            const x = Math.random() * this.canvas.width;
            const y = Math.random() * this.canvas.height;
            const directionX = (Math.random() - 0.5) * 0.5;
            const directionY = (Math.random() - 0.5) * 0.5;
            const color = this.getRandomColor();
            
            this.particles.push({
                x, y, size, directionX, directionY, color,
                baseX: x, baseY: y
            });
        }
    }

    getRandomColor() {
        const colors = [
            'rgba(99, 102, 241, 0.8)',   // Primary
            'rgba(236, 72, 153, 0.8)',   // Secondary  
            'rgba(6, 182, 212, 0.8)',    // Accent
            'rgba(129, 140, 248, 0.6)',  // Primary light
        ];
        return colors[Math.floor(Math.random() * colors.length)];
    }

    addEventListeners() {
        window.addEventListener('resize', () => this.resize());
        
        this.canvas.addEventListener('mousemove', (e) => {
            this.mouse.x = e.x;
            this.mouse.y = e.y;
        });

        this.canvas.addEventListener('mouseleave', () => {
            this.mouse.x = null;
            this.mouse.y = null;
        });
    }

    drawParticle(particle) {
        this.ctx.beginPath();
        this.ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        this.ctx.fillStyle = particle.color;
        this.ctx.fill();
    }

    connectParticles() {
        for (let a = 0; a < this.particles.length; a++) {
            for (let b = a + 1; b < this.particles.length; b++) {
                const dx = this.particles[a].x - this.particles[b].x;
                const dy = this.particles[a].y - this.particles[b].y;
                const distance = Math.sqrt(dx * dx + dy * dy);

                if (distance < 120) {
                    const opacity = 1 - (distance / 120);
                    this.ctx.strokeStyle = `rgba(99, 102, 241, ${opacity * 0.15})`;
                    this.ctx.lineWidth = 1;
                    this.ctx.beginPath();
                    this.ctx.moveTo(this.particles[a].x, this.particles[a].y);
                    this.ctx.lineTo(this.particles[b].x, this.particles[b].y);
                    this.ctx.stroke();
                }
            }
        }
    }

    updateParticle(particle) {
        // Boundary check
        if (particle.x + particle.size > this.canvas.width || particle.x - particle.size < 0) {
            particle.directionX = -particle.directionX;
        }
        if (particle.y + particle.size > this.canvas.height || particle.y - particle.size < 0) {
            particle.directionY = -particle.directionY;
        }

        // Mouse interaction
        if (this.mouse.x !== null && this.mouse.y !== null) {
            const dx = this.mouse.x - particle.x;
            const dy = this.mouse.y - particle.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance < this.mouse.radius) {
                const force = (this.mouse.radius - distance) / this.mouse.radius;
                const directionX = dx / distance;
                const directionY = dy / distance;
                particle.x -= directionX * force * 2;
                particle.y -= directionY * force * 2;
            }
        }

        // Natural movement
        particle.x += particle.directionX;
        particle.y += particle.directionY;
    }

    animate() {
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        for (const particle of this.particles) {
            this.updateParticle(particle);
            this.drawParticle(particle);
        }

        this.connectParticles();
        requestAnimationFrame(() => this.animate());
    }
}

// Export for use
if (typeof module !== 'undefined' && module.exports) {
    module.exports = ParticleSystem;
}
